package chap04;

import java.util.Scanner;

public class SwitchExam3 {

	public static void main(String[] args) {
		
		// TODO Auto-generated method stub
		
		Scanner in = new Scanner(System.in);
		System.out.println("A ~ E 까지 문자를 입력하세요.(대소문자구별X)");
		
		char grade = 'f';
		
		switch (grade) {
		
		case 'A' :
		case 'a' :
			System.out.println("VIP 회원 입니다.");
			break;
			
		case 'B' :
		case 'b' :
			System.out.println("GOLD 회원 입니다.");
			break;
			
		case 'C' :
		case 'c' :
			System.out.println("SLIVER 회원 입니다.");
			break;
	
		case 'D' :
		case 'd' :
			System.out.println("BLOZE 회원 입니다.");
			break;
			
		case 'E' :
		case 'e' :
			System.out.println("YELLOW 회원 입니다.");
			break;	
			
		default :
			System.out.println(" Unknown 입니다.");
			System.out.println(" 회원가입을 하시겠습니까 ? ");
		
		} //스위치문 종료
	
		
		
	}	// 메서드 종료

}	//클래스 종료
